const main = async () => {
  const browser = await puppeteer.launch();
  const page = await browser.newPage();
  await page.goto(url);

  const allArticles = await page.evaluate(() => {
    const articles = Array.from(document.querySelectorAll(".title-box"));
    return articles.slice(0, 9).map((article) => {
      const anchor = article.querySelector("a.vijesti-text-hover");
      return anchor.href;
    });
  });
  for (const link of allArticles) {
    const articlePage = await browser.newPage();
    await articlePage.goto(link);
    const articleData = await articlePage.evaluate(() => {
      const title = document
        .querySelector("h1.vijesti-text-parsed")
        .textContent.trim();
      const image = document.querySelector("img.img-responsive").src;
      const paragraphs = document.querySelectorAll(
        "div.vijesti-link-underline p"
      );
      const content = Array.from(paragraphs)
        .map((p) => p.textContent.trim())
        .join(" ");

      return {
        title,
        image,
        content,
      };
    });
    const articleExists = allData.some(
      (data) => data.title === articleData.title
    );

    if (!articleExists) {
      allData.unshift(articleData);
    }

    await articlePage.close();
  }

  await browser.close();
};
cron.schedule("*/5 * * * * *", () => {
  main();
});

app.get("/", async (req, res) => {
  try {
    const data = allData;
    console.log(data);
    res.json(allData);
  } catch {}
});
app.listen(PORT, () => {
  console.log(`Server is running on port ${PORT}`);
});
